css:
.receipt-header {
    text-align: center;
    margin-bottom: 30px;
}

.receipt-header h2 {
    font-size:30px;
    font-weight: bold;

}

.receipt-header p {
    font-size:20px;
}

.receipt-details {
    margin-bottom: 20px;
}

.receipt-details p {
    font-size: 20px;
}

.receipt-footer {
    text-align: center;
    margin-top: 50px;
    font-size: 20px;
}

.owner-signature{
    text-align: end;
    font-size: 20px;
    text-decoration-line: underline;
}

.line{
    display: flex;
    position: relative;
    left: 76%;
    background-color: black;
    height: 2px;
    width: 166px;
}
